          <Link to="/student/dashboard" className="hover:underline">üè† Dashboard</Link>
          <Link to="/student/profile" className="hover:underline">üë§ Perfil</Link>
        </div>
        <button onClick={logout} className="bg-red-500 px-3 py-1 rounded hover:bg-red-600">
          Cerrar Sesi√≥n
        </button>
      </nav>

      <main className="p-4">{children}</main>
    </div>
  );
};

export default StudentLayout;
