import React, { useEffect, useState } from 'react';
import axios from 'axios';
import StudentLayout from '../../layouts/StudentLayout';

const StudentProfile = () => {
  const [user, setUser] = useState(null);
  const [degreesList, setDegreesList] = useState([]);
  const [newPhone, setNewPhone] = useState('');
  const [selectedDegree, setSelectedDegree] = useState('');

  const cif = localStorage.getItem('cif');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const [userRes, degRes] = await Promise.all([
          axios.get(`http://localhost:8087/uam-carnet-sys/user/byCif=${cif}`),
          axios.get('http://localhost:8087/uam-carnet-sys/degree'),
        ]);

        // Normalizar wrapper ResponseApiDTO o array directo
        const payload = userRes.data;
        const actualUser = Array.isArray(payload.data)
          ? payload.data[0]
          : payload;
        setUser(actualUser);
        setNewPhone(actualUser.phoneNumber || '');

        // grados disponibles para añadir
        setDegreesList(degRes.data);
      } catch (err) {
        console.error('Error al cargar perfil:', err);
      }
    };

    fetchProfile();
  }, [cif]);

  if (!user) {
    return (
      <StudentLayout>
        <div className="text-center mt-10">Cargando perfil...</div>
      </StudentLayout>
    );
  }

  return (
    <StudentLayout>
      <div className="max-w-4xl mx-auto mt-10 p-6 bg-white rounded shadow">
        <h1 className="text-2xl font-bold mb-4">
          Perfil de {user.names} {user.surnames}
        </h1>

        <section className="mb-6">
          <h2 className="text-xl font-semibold mb-2">👤 Información</h2>
          <p><strong>CIF:</strong> {user.cif}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Rol:</strong> {user.role}</p>
          <p><strong>Tipo:</strong> {user.type}</p>
        </section>

        <section className="mb-6">
          <h2 className="text-xl font-semibold mb-2">📞 Teléfono</h2>
          <div className="flex items-center space-x-2">
            <input
              type="text"
              value={newPhone}
              onChange={e => setNewPhone(e.target.value)}
              className="border border-gray-300 rounded p-2 flex-grow"
            />
            <button
              onClick={async () => {
                try {
                  await axios.patch(
                    `http://localhost:8087/uam-carnet-sys/user/cif=${cif}/setNumber=${newPhone}`
                  );
                  alert('Número actualizado exitosamente');
                } catch (err) {
                  console.error('Error al actualizar número:', err);
                }
              }}
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Actualizar
            </button>
          </div>
        </section>

        <section className="mb-6">
          <h2 className="text-xl font-semibold mb-2">🎓 Carreras Actuales</h2>
          {user.degrees?.length > 0 ? (
            <ul className="list-disc ml-5 space-y-1">
              {user.degrees.map(deg => (
                <li key={deg.id} className="flex justify-between items-center">
                  {deg.degreeName}
                  <button
                    onClick={async () => {
                      try {
                        await axios.patch(
                          `http://localhost:8087/uam-carnet-sys/user/cif=${cif}/removeDegree=${deg.id}`
                        );
                        alert('Carrera eliminada');
                        // podrías refrescar user.degrees aquí
                      } catch (err) {
                        console.error('Error al eliminar carrera:', err);
                      }
                    }}
                    className="text-sm text-red-600 hover:underline"
                  >
                    Eliminar
                  </button>
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-yellow-600">No tienes carreras activas.</p>
          )}
        </section>

        <section>
          <h2 className="text-xl font-semibold mb-2">➕ Añadir Nueva Carrera</h2>
          <div className="flex items-center space-x-2">
            <select
              className="border border-gray-300 rounded p-2 flex-grow"
              value={selectedDegree}
              onChange={e => setSelectedDegree(e.target.value)}
            >
              <option value="">Seleccione una carrera</option>
              {degreesList.map(deg => (
                <option key={deg.id} value={deg.id}>
                  {deg.degreeName}
                </option>
              ))}
            </select>
            <button
              onClick={async () => {
                if (!selectedDegree) return alert('Seleccione una carrera');
                try {
                  await axios.patch(
                    `http://localhost:8087/uam-carnet-sys/user/cif=${cif}/setDegree=${selectedDegree}`
                  );
                  alert('Carrera añadida correctamente');
                  // podrías refrescar user.degrees aquí
                } catch (err) {
                  console.error('Error al añadir carrera:', err);
                }
              }}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
            >
              Añadir
            </button>
          </div>
        </section>
      </div>
    </StudentLayout>
  );
};

export default StudentProfile;
